// JavaScript Document
function getStyle(obj,name){
  if(name=='opacity'){
    if(obj.currentStyle){
      return parseFloat(obj.currentStyle[name]);
    }
    else{
      return parseFloat(getComputedStyle(obj,false)[name]);
    }
  }
  else{
    if(obj.currentStyle){
      return parseInt(obj.currentStyle[name]);
    }
    else{
      return parseInt(getComputedStyle(obj,false)[name]);
    }
  }
}

function moveFrame(obj,json,funEnd){
    clearInterval(obj.timer);
    obj.timer=setInterval(function(){
    var bStop=true;//假设所有的值都到了。
    for(var attr in json){ 
  
      var cur=null;
      if(attr=='opacity'){
        cur=Math.round(parseFloat(getStyle(obj,attr)*100));
        
      }
      else{
        cur=parseInt(getStyle(obj,attr));
        
      }
      var speed=(json[attr]-cur)/6;
      speed=speed>0?Math.ceil(speed):Math.floor(speed);
      if(cur!=json[attr]){
        bStop=false;
      }
      if(attr=='opacity'){
        
        obj.style['filter']='alpha(opacity:'+(cur+speed)+')';
        obj.style[attr]=(cur+speed)/100;
      }
      else
      {
      obj.style[attr]=cur+speed+'px';
      }
      
    }
    if(bStop)
      {
        clearInterval(obj.timer);
        if(funEnd)
        {
          funEnd();
        }
      }
  },30);
}